// https://leetcode.com/problems/is-subsequence

package leetcode;


import org.junit.Test;

import java.util.ArrayDeque;
import java.util.ArrayList;
import java.util.List;
import java.util.Queue;

import static org.junit.Assert.assertEquals;

public class No392_IsSubsequence {

    @Test
    public void test() throws Exception {
        No392_IsSubsequence instance = new No392_IsSubsequence();
        assertEquals(true, instance.isSubsequence("ace", "abcde"));
        assertEquals(true, instance.isSubsequence("ace", "abcdea"));
        assertEquals(false, instance.isSubsequence("adc", "abcdea"));
        assertEquals(true, instance.isSubsequence("abc", "ahbgdc"));
        assertEquals(false, instance.isSubsequence("axc", "ahbgdc"));
        assertEquals(false, instance.isSubsequence("acb", "ahbgdc"));

        assertEquals(true, instance.isSubsequence("leeeeetcode", ""));
    }

    public boolean isSubsequence(String s, String t) {
        List<ArrayDeque<Integer>> S = new ArrayList<>(26);
        for (int i = 0; i < 26; i++) {
            S.add(new ArrayDeque<>());
        }

        char[] tc = t.toCharArray();
        for (int i = 0; i < tc.length; i++) {
            char c = tc[i];
            int idx = c - 'a';
            ArrayDeque queue = S.get(idx);
            queue.add(i);
        }

        int last = -1;
        int length = s.length();
        for (int i = 0; i < length; i++) {
            char c = s.charAt(i);
            int idx = c - 'a';
            ArrayDeque queue = S.get(idx);

            boolean found = false;
            while (!queue.isEmpty()) {
                int _last = (Integer) queue.removeFirst();
                if (_last > last) {
                    last = _last;
                    found = true;
                    break;
                }
            }
            if (!found) return false;
        }
        return true;
    }
}
